import React, { useState, useEffect } from 'react';
import Joke from './components/Joke';
import Votes from './components/Votes';
import NextJoke from './components/NextJoke';

function App() {
    const [joke, setJoke] = useState(null);
    const [shownJokes, setShownJokes] = useState(new Set());

    const fetchJoke = (id = null) => {
        const url = id ? `http://localhost:5000/api/joke/${id}` : "http://localhost:5000/api/joke";
        fetch(url)
            .then(response => response.json())
            .then(data => {
                if (shownJokes.has(data._id)) {
                    console.log("Duplicate joke detected, refetching...");
                    if (retryCount < 5) {
                        fetchJoke(null, retryCount + 1);
                    } else {
                        console.warn("Too many retries, showing duplicate joke.");
                        setJoke(data);
                    }
                } else {
                    setJoke(data);
                    setShownJokes(prev => new Set(prev).add(data._id)); // Добавляем ID в список
                }
            })
            .catch(error => console.error("Error fetching joke:", error));
    };

    useEffect(() => {
        fetchJoke();
    }, []);

    return (
        <div>
            {joke && (
                <>
                    <Joke joke={joke} />
                    <Votes jokeId={joke._id} />
                    <NextJoke fetchJoke={fetchJoke} />
                </>
            )}
        </div>
    );
}

export default App;
